function setDimensions(){var n=$(window).height();$(".splash-container").css("min-height",n+"px")}function showLogin(){$("#logon").fadeIn("fast")}function hideLogin(){$("#logon").fadeOut("fast")}function animateTimeline(){var n=$("#ani-timeline");n.find(".ani-header").velocity("transition.slideDownIn",{duration:1e3}),n.find(".ani-body").velocity("transition.slideUpBigIn",{duration:1e3}),n.find(".ani-buttons").velocity("transition.fadeIn",{delay:5e3}),n.find(".ani-iphone-image-container").velocity("transition.slideUpBigIn",{delay:2e3}),n.find(".ani-iphone-content-image").velocity({"background-position-y":-750},{duration:2e3,delay:1500,easing:"ease-in-out",complete:function(){n.find(".ani-iphone-content-image").velocity({"background-position-y":0},{duration:1e3,delay:500,easing:"ease-in-out"})}})}$(document).ready(function(){setDimensions(),$(window).resize(function(){setDimensions()}),$("#login-link").click(function(){showLogin()}),$("#logon").click(function(){hideLogin()}),animateTimeline()});